import React, {Fragment, useState, useEffect} from "react";
import { StyleSheet, Text, View, TextInput, Button} from 'react-native';
import { Table, TableWrapper, Row, Rows, Col, Cols, Cell } from 'react-native-table-component';
import { FlatList } from "react-native-gesture-handler";


// import Items from "./Items";

const Lists=() =>{
    const [lists, setLists]= useState([]);

    const deleteList= async id =>{
        try{
            const deleteList=await fetch(`http://localhost:5000/deletelist/${id}`,{
                method: "DELETE"
            });

            setLists(lists.filter(list=>list.list_id !==id));
        }catch(err){
            console.error(err.message);
        }
    }

    const getLists=async()=>{
        try{
            const response= await fetch("http://localhost:5000/lists");
            const jsonData= await response.json();

            setLists(jsonData);
            console.log(jsonData);
        }catch(err){
            console.error(err.message);
        }
    };

    useEffect(()=> {
        getLists();
    }, []);

    console.log(lists);

    const head=["List Id", "Description", "Delete"];

    return(
        <Fragment>
            <Table borderStyle={{borderWidth: 2, borderColor: '#c8e1ff'}}>
                <Row data={head}></Row>

                    {lists.map((list)=>(
                        <TableWrapper key={list.list_id} style={styles.row}>
                        <Cell data={list.list_id}/>    
                        <Cell data={list.description}/>



                        <Cell textStyle={styles.text}
                        data={<Button titleStyle={styles.btnText} 
                        buttonStyle={styles.btn}
                        title="Delete" onPress={()=>deleteList(list.list_id)}/>}/>


                        </TableWrapper>
                    ))}

            </Table>
        </Fragment>
    );
};

const styles = StyleSheet.create({
    container: { flex: 1, padding: 16, paddingTop: 30, backgroundColor: '#fff' },
  head: { height: 40, backgroundColor: '#808B97' },
  text: { margin: 6 },
  row: { flexDirection: 'row', backgroundColor: '#FFF1C1' },
  btn: { width: 58, height: 18, backgroundColor: '#78B7BB',  borderRadius: 2 },
  btnText: { textAlign: 'center', color: '#fff' }
  });

export default Lists;
