import { View } from "react-native";
import { FlatList } from "react-native-gesture-handler";

const Items=({list})=>{
    const [items, setItems]= useState([]);
    const getItems= async()=>{
        try {
            const response=await fetch(`http://localhost:5000/items/${list.list_id}`,{method:"GET"});
            const jsonData=await response.json();
            setItems(jsonData);
        } catch (err) {
            console.error(err.message);
        };
    };

    useEffect(() => {
        getItems();
      }, []);





    return (
        <View>
            <Text>list.list_id</Text>
            <Table borderStyle={{borderWidth: 2, borderColor: '#c8e1ff'}}>

                    {items.map((item)=>(
                        <TableWrapper key={item.item_id} style={styles.row}>

                        <Cell data={item.description}/>
                        <Cell data={item.done}/>
                        <Cell data="Delete"/>


                        {/* <Col>
                        Delete
                        <Button onPress={()=>deleteList(list.list_id)}>Delete 
                            </Button>
                            </Col> */}
                        </TableWrapper>
                    ))}

            </Table>



        </View>
    )
}

const styles = StyleSheet.create({
    container: { flex: 1, padding: 16, paddingTop: 30, backgroundColor: '#fff' },
    head: { height: 40, backgroundColor: '#808B97' },
    text: { margin: 6 },
    row: { flexDirection: 'row', backgroundColor: '#FFF1C1' },
    btn: { width: 58, height: 18, backgroundColor: '#78B7BB',  borderRadius: 2 },
    btnText: { textAlign: 'center', color: '#fff' }
  });

export default Items;
